const objeto = (id, nombre, desc, stock) => ({
    id: id,
    nombre: nombre,
    desc: desc,
    stock: stock
})

const promesa = new Promise((res, rej) => {

    setTimeout(() => {

        res(producto)

    }, 3000);


})


promesa.then((result) => {

    console.log(result)

})
